CREATE TABLE IF NOT EXISTS `clients` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `nom` varchar(30) CHARACTER SET utf8 NOT NULL,
  `prenom` varchar(30) CHARACTER SET utf8 DEFAULT NULL,
  `adresse` varchar(250) CHARACTER SET utf8 DEFAULT NULL,
  `cp` varchar(10) CHARACTER SET utf8 DEFAULT NULL,
  `ville` varchar(30) CHARACTER SET utf8 DEFAULT NULL,
  `tel` varchar(15) CHARACTER SET utf8 DEFAULT NULL,
  `mobile` varchar(15) CHARACTER SET utf8 DEFAULT NULL,
  `email` varchar(50) CHARACTER SET utf8 DEFAULT NULL,
  `createdAt` datetime NOT NULL,
  `updatedAt` datetime NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin1 AUTO_INCREMENT=2 ;


CREATE TABLE IF NOT EXISTS `fournisseurs` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `nom` varchar(30) CHARACTER SET utf8 NOT NULL,
  `adresse1` varchar(250) CHARACTER SET utf8 DEFAULT NULL,
  `adresse2` varchar(250) CHARACTER SET utf8 DEFAULT NULL,
  `cp` varchar(10) CHARACTER SET utf8 DEFAULT NULL,
  `ville` varchar(30) CHARACTER SET utf8 DEFAULT NULL,
  `tel` varchar(15) CHARACTER SET utf8 DEFAULT NULL,
  `fax` varchar(15) CHARACTER SET utf8 DEFAULT NULL,
  `mobile` varchar(15) CHARACTER SET utf8 DEFAULT NULL,
  `email` varchar(50) CHARACTER SET utf8 DEFAULT NULL,
  `createdAt` datetime NOT NULL,
  `updatedAt` datetime NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin1 AUTO_INCREMENT=2 ;


CREATE TABLE IF NOT EXISTS `produits` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `nom` varchar(40) CHARACTER SET utf8 NOT NULL,
  `id_type` varchar(20) CHARACTER SET utf8 NOT NULL,
  `ref_interne` varchar(20) CHARACTER SET utf8 NOT NULL,
  `ref_externe` varchar(30) CHARACTER SET utf8 NOT NULL,
  `pht` decimal(5,2) NOT NULL,
  `tva` decimal(5,2) NOT NULL,
  `ttc_externe` decimal(5,2) NOT NULL,
  `tx_com` decimal(5,2) NOT NULL,
  `ttc_vente` decimal(5,2) NOT NULL,
  `icone` varchar(200) CHARACTER SET utf8 NOT NULL,
  `conditionnement` varchar(10) CHARACTER SET utf8 NOT NULL,
  `disponibilite` int(11) DEFAULT NULL,
  `createdAt` datetime NOT NULL,
  `updatedAt` datetime NOT NULL,
  PRIMARY KEY (`id`),
  KEY `id_type` (`id_type`),
  KEY `ref_externe` (`ref_externe`),
  KEY `disponibilite` (`disponibilite`)
) ENGINE=InnoDB  DEFAULT CHARSET=latin1 AUTO_INCREMENT=7 ;


CREATE TABLE IF NOT EXISTS `typesproduits` (
  `id` varchar(20) CHARACTER SET utf8 NOT NULL,
  `nom` varchar(100) NOT NULL,
  `createdAt` datetime NOT NULL,
  `updatedAt` datetime NOT NULL,
  PRIMARY KEY (`id`),
  KEY `id_type` (`id`)
) ENGINE=InnoDB DEFAULT CHARSET=latin1;


